import React, { useContext } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import { ViewsDataContext } from './App'; // Adjust the import path according to your file structure

const EditComponent = () => {
  const { updateViewItem } = useContext(ViewsDataContext);
  const navigate = useNavigate();
  const { id } = useParams();
  const location = useLocation();
  const { item } = location.state || {}; // Assuming the item is passed in location.state

  if (!item) {
    // Fallback if no item is found in the state, e.g., directly navigating to the edit URL
    return <div>Item not found. Please go back to the home page and select an item to edit.</div>;
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedItem = {
      ...item,
      name: e.target.name.value,
      whyImportant: e.target.whyImportant.value,
    };

    updateViewItem(updatedItem);
    navigate('/home');
  };

  return (
    <div id="content">
      <button onClick={() => navigate(-1)}>Go Back</button>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" defaultValue={item.name} required />
        <label htmlFor="whyImportant">Why Important:</label>
        <textarea id="whyImportant" name="whyImportant" defaultValue={item.whyImportant} required></textarea>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default EditComponent;
