import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Home = ({ viewsData }) => {
  const location = useLocation();
  // Assuming `username` is a key in the object provided to `state`
  const { username } = location.state || {}; // Add a default empty object to avoid destructuring `null` or `undefined`


  return (
    <div id="content">
      <section className="navTop" style={{ marginBottom: '5rem' }}>
        <header>
          <nav>
            <Link style={{ display: 'flex', alignItems: 'center', fontWeight: 'bold', color: "black" }} to={"/signin"}>
              <span className="material-symbols-outlined">chevron_left</span> <h5>Logout</h5>
            </Link>
            <h1 className="logo">Feed</h1>
          </nav>
        </header>
      </section>

      <section style={{ padding: '1rem', margin: ' auto 0rem' }}>
        <div style={{ margin: '3rem auto', padding: '0rem', backgroundColor: 'rgb(0, 0, 0)', marginBottom: '0' }}>
        <h1 style={{ fontSize: '5vh', fontWeight: 'bolder', color: 'white',padding:'2rem' }}>Welcome, {username}</h1>
          <hr style={{ padding: '0', margin: '0', height: '2.5rem', width: '100%' }} className="gradient" />
        </div>

        <div style={{
  display: 'flex',
  flexDirection: 'column', // Keeps cards in a vertical column
  gap: '20px',
  margin: '3rem',
}}>
  {viewsData.map((view, index) => (
    <div key={view.id} style={{
      display: 'flex',
      justifyContent: index % 2 === 0 ? 'flex-start' : 'flex-end', // Positions card at start or end
      width: '100%', // Container takes full width
    }}>
      <div style={{
        backgroundColor: 'black',
        padding: '20px',
        width: '50%', // Standard width for all cards
      }}>
        <Link
          to={`/view/${view.id}`}
          state={{ item: view }}
          style={{
            color: 'white',
            textDecoration: 'none',
            display: 'block', // Ensures the link fills the card
          }}
        >
          <h1 style={{ fontSize: '2rem', fontWeight: 'bold' }}>{view.name}</h1>
          <h2 style={{ fontSize: '1rem', fontWeight: '100' }}>{view.whyImportant}</h2>
        </Link>
        <hr style={{ padding: '0', margin: '2px 0 0 0', height: '2px', width: '100%', backgroundColor: 'white' }} />
      </div>
    </div>
  ))}
</div>





      </section>
      {/* Floating Action Button for Adding a New Note */}
      <Link to="/create" style={{
        position: 'fixed',
        right: '20px',
        bottom: '20px',
        backgroundColor: 'black', // Example blue color
        color: 'white',
        padding: '15px',
        borderRadius: '50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        textDecoration: 'none',
        fontSize: '24px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
        zIndex: '1000', // Ensure it floats above other content
      }}>
        <span className="material-symbols-outlined">add</span>
      </Link>
    </div>
  );
};

export default Home;
