import React from 'react';
import { useParams, Link } from 'react-router-dom';


// Assuming viewsData is available globally, imported, or fetched from an API
import { viewsData } from './viewsData'; // Adjust the import path as necessary

const ViewComponent = () => {
  const { id } = useParams();
  const viewItem = viewsData.find(item => item.id.toString() === id);

  if (!viewItem) {
    return <div>Item not found</div>;
  }

  return (
    <div id="content">
      <section className="navTop">
        <header>
          <nav>
            <Link to="/home">
              <button><span className="material-symbols-outlined">chevron_left</span></button>
            </Link>
            <h1 className="logo">Feed</h1>
          </nav>
        </header>
      </section>

      <section style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
        <div style={{ height: '50vh', width: '100%', backgroundImage: "url('./bg.jpg')", backgroundSize: 'cover', padding: '0rem' }}></div>
        <div style={{ backgroundColor: 'rgb(109, 109, 109)', width: '100%' }}>
          <div style={{ display: 'flex', justifyContent: 'right', position: 'absolute', right: '20px' }}>

          </div>
          <div className="text" style={{ padding: '1rem' }}>
            <h1>{viewItem.name}</h1>
            <h2 style={{ fontWeight: '100', fontSize: '16px' }}>{viewItem.whyImportant}</h2>
          </div>
          <div style={{ backgroundColor: 'blueviolet', padding: '9px', margin: '0' }}></div>
        </div>
      </section>
    </div>
  );
};

export default ViewComponent;
