// src/components/SignIn.js
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function SignIn() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleSubmit = (event) => {
    event.preventDefault();
    // Here you could also add logic to authenticate the user
    // For now, we'll just navigate to /home and pass the username
    history.push('/home', { username: username });
  };

  return (
    <section className="signin">
      <div id="imgtop" className="gradient">
        <h1 className="logo">Feed</h1>
      </div>
      <form className="signinfrm" onSubmit={handleSubmit}>
        <div id="inputs">
          <label htmlFor="username">Name</label>
          <input
            id="username"
            name="username"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label htmlFor="password">Password</label>
          <input
            id="password"
            name="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <div className='btnenter'>
            <button type="submit" className="btns">
              <span className="material-icons">arrow_forward</span>
            </button>
          </div>
        </div>
      </form>
    </section>
  );
}

export default SignIn;
