import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Home = ({ viewsData }) => {
  const location = useLocation();
  const username = location.state ? location.state.username : "User"; // Fallback to "User" if no username is provided

  return (
    <div id="content">
      <section className="navTop">
        <div style={{ marginBottom: '5rem' }}>
          <header>
            <nav>
              <button style={{ display: 'flex', alignItems: 'center', fontWeight: 'bold' }} onClick={() => {/* Logout logic */}}>
                <span className="material-symbols-outlined">chevron_left</span> Logout
              </button>
              <h1 className="logo">Feed</h1>
            </nav>
          </header>
        </div>
      </section>

      <section style={{ padding: '1rem' }}>
        <div style={{ display: 'flex', justifyContent: 'left', flexDirection: 'column', width: '80vw', marginLeft: '0' }}>
          <div style={{ padding: '2rem', backgroundColor: 'rgb(0, 0, 0)', marginBottom: '0' }}>
            <h1 style={{ fontSize: '5vh', fontWeight: 'bolder', color: 'white' }}>Welcome</h1>
            <h2 style={{ fontSize: '2vh', fontWeight: '100', color: 'white' }}>{username}</h2>
          </div>
          <hr style={{ padding: '0', margin: '0', height: '2.5rem', width: '100%' }} className="gradient" />
        </div>
        
        // Inside Home component
{viewsData.map(view => (
  <Link 
    to={`/view/${view.id}`}
    state={{ item: view }} // Passing the entire view item in the state
    key={view.id}
    style={{ color: 'black', textDecoration: 'none' }}
  >
    <div style={{ backgroundColor: 'black', marginTop: '3rem', width: '80vw', padding: '20px' }}>
      <h1 style={{ color: 'white', fontSize: '2rem', fontWeight: 'bold' }}>{view.name}</h1>
      <h2 style={{ color: 'white', fontSize: '1rem', fontWeight: '100' }}>{view.whyImportant}</h2>
    </div>
  </Link>
))}

      </section>
    </div>
  );
};

export default Home;
