import React, { useState, createContext } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './home';
import SignIn from './signin';
import ViewComponent from './viewComponent';
import EditComponent from './EditComponent';

// Create a context for the views data and the function to update it
export const ViewsDataContext = createContext();

const initialViewsData = [
  {
    id: 1,
    slug: "learn-react",
    name: "Learn React",
    whyImportant: "Mastering React is crucial for modern web development, allowing for the creation of dynamic and responsive user interfaces."
  },
  {
    id: 2,
    slug: "contribute-open-source",
    name: "Contribute to Open Source",
    whyImportant: "Contributing to open source projects can enhance your coding skills, expand your professional network, and increase your visibility in the tech community. It's a way to give back and learn simultaneously."
  },
  {
    id: 3,
    slug: "achieve-aws-certification",
    name: "Achieve AWS Certification",
    whyImportant: "Earning an AWS certification validates your cloud expertise, opens up new career opportunities, and can lead to higher salaries. It's a step towards demonstrating your commitment to professional growth in the cloud technology space."
  }
];

const App = () => {
  const [viewsData, setViewsData] = useState(initialViewsData);

  // Function to update an item in the viewsData array
  const updateViewItem = (updatedItem) => {
    const newData = viewsData.map(item =>
      item.id === updatedItem.id ? updatedItem : item
    );
    setViewsData(newData);
  };

  return (
    <Router>
      <ViewsDataContext.Provider value={{ viewsData, updateViewItem }}>
        <Routes>
          <Route path="/" element={<SignIn />} />
          <Route path="/home" element={<Home viewsData={viewsData} />} />
          <Route path="/view/:id" element={<ViewComponent />} />
          <Route path="/edit/:id" element={<EditComponent />} />
        </Routes>
      </ViewsDataContext.Provider>
    </Router>
  );
};

export default App;
