import React, { useContext } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { ViewsDataContext } from './App'; // Adjust the import path according to your file structure

const EditComponent = () => {
  const { updateViewItem } = useContext(ViewsDataContext);
  const navigate = useNavigate();
  const { id } = useParams();
  const { item } = useContext(ViewsDataContext).viewsData.find(item => item.id === Number(id)) || {};

  if (!item) {
    return <div>Item not found. Please go back to the home page and select an item to edit.</div>;
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedItem = {
      ...item,
      name: e.target.name.value,
      whyImportant: e.target.whyImportant.value,
    };

    updateViewItem(updatedItem);
    navigate('/home');
  };

  return (
    <div id="content" style={{ fontFamily: "'Roboto', sans-serif" }}>
      <section className="navTop" style={{ marginBottom: '5rem' }}>
        <header>
          <nav>
            <button onClick={() => navigate(-1)} style={{ cursor: 'pointer', background: 'none', border: 'none' }}>
              <span className="material-symbols-outlined" style={{ fontSize: '24px' }}>
                chevron_left
              </span>
            </button>
            <h1 className="logo" style={{ display: 'inline' }}>Edit</h1>
          </nav>
        </header>
      </section>
  
      <section style={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', alignItems: 'center' }}>
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', alignItems: 'center', width: '50%' }}>
          <input type="hidden" name="id" value={item.id} />
          
          <div style={{ margin: '10px', display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', width: '100%' }}>
            <label htmlFor="name" style={{ margin: '10px 0', fontWeight: '400', fontSize: '15px' }}>Name:</label>
            <input type="text" id="name" name="name" defaultValue={item.name} required style={{ backgroundColor: 'black', color: 'white', border: 'none', height: '28px' }} />
            <hr style={{ border: '1px solid black', width: '100%', marginTop: '7px' }} />
          </div>
          
          <div style={{ margin: '10px', display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', width: '100%' }}>
            <label htmlFor="whyImportant" style={{ margin: '10px 0', fontWeight: '400', fontSize: '15px' }}>Information:</label>
            <textarea id="whyImportant" name="whyImportant" defaultValue={item.whyImportant} required style={{ backgroundColor: 'black', color: 'white', border: 'none', height: '20vh', resize: 'none' }} />
            <hr style={{ border: '1px solid black', width: '100%', marginTop: '7px' }} />
          </div>
          
          <button type="submit" style={{ height: '50px', width: '50px', backgroundColor: 'black', borderRadius: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <span className="material-symbols-outlined" style={{ color: 'white', fontSize: '24px' }}>
              check
            </span>
          </button>
        </form>
      </section>
    </div>
  );
};

export default EditComponent;
