import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const EditComponent = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { item } = location.state || {}; // Accessing the passed item or providing an empty object as fallback

  const handleSubmit = (e) => {
    e.preventDefault();
    // Implementation for submitting form data
    // This could involve sending a PUT request to your backend to update the item
    console.log('Form submitted', { id: item.id, name: e.target.name.value, whyImportant: e.target.whyImportant.value });
    
    navigate('/home'); // Redirecting back to home or another appropriate view after form submission
  };

  if (!item) {
    return <div>Item not found</div>;
  }

  return (
    <div id="content">
      <section className="navTop">
        <header>
          <nav>
            <button onClick={() => navigate(-1)}><span className="material-symbols-outlined">
                chevron_left
              </span></button>
            <h1 className="logo">Edit</h1>
          </nav>
        </header>
      </section>
  
      <section
        style={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', alignItems: 'center' }}>
  
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', alignItems: 'center', width: '50%' }}>
  
          {/* Hidden input for ID (if editing an existing item) */}
          <input type="hidden" name="id" value={item.id} />
          
          <div style={{ margin: '10px', display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', width: '100%' }}>
            <label htmlFor="name">Name:</label>
            <input style={{ backgroundColor: 'black', color: 'white', border: 'none', height: '28px' }} type="text" id="name" name="name" defaultValue={item.name} required />
            <hr style={{ border: '1px solid black', width: '100%', marginTop: '7px' }} />
          </div>
          <div style={{ margin: '10px', display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', width: '100%' }}>
            <label htmlFor="whyImportant">Information:</label>
            <textarea style={{ backgroundColor: 'black', color: 'white', border: 'none', height: '20vh', resize: 'none' }} id="whyImportant" name="whyImportant" defaultValue={item.whyImportant} required />
            <hr style={{ border: '1px solid black', width: '100%', marginTop: '7px' }} />
          </div>
          
          <button type="submit"
            style={{ height: '50px', width: '50px', backgroundColor: 'black', borderRadius: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <span style={{ color: 'white' }} className="material-symbols-outlined">
              check
            </span>
          </button>
        
        </form>
      </section>
    </div>
  );
};

export default EditComponent;
