import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Home = ({ viewsData }) => {
  const location = useLocation();
  // Assuming `username` is a key in the object provided to `state`
  const { username } = location.state || {}; // Add a default empty object to avoid destructuring `null` or `undefined`


  return (
    <div id="content">
      <section className="navTop" style={{ marginBottom: '5rem' }}>
        <header>
          <nav>
            <Link style={{ display: 'flex', alignItems: 'center', fontWeight: 'bold', color: "black" }} to={"/signin"}>
              <span className="material-symbols-outlined">chevron_left</span> <h5>Logout</h5>
            </Link>
            <h1 className="logo">Feed</h1>
          </nav>
        </header>
      </section>

      <section style={{ padding: '1rem', margin: ' 3rem auto' }}>
        <div style={{ margin: '3rem auto', padding: '2rem', backgroundColor: 'rgb(0, 0, 0)', marginBottom: '0' }}>
        <h1 style={{ fontSize: '5vh', fontWeight: 'bolder', color: 'white' }}>Welcome, {username}</h1>
          <hr style={{ padding: '0', margin: '0', height: '2.5rem', width: '100%' }} className="gradient" />
        </div>


        <div style={{
  display: 'flex',
  flexDirection: 'column', // Arranges items in a column
  gap: '20px',
  margin: '3rem auto',
  alignItems: 'stretch' // Ensures that the links stretch to fill the container width
}}>
  {viewsData.map((view, index) => (
    <Link
      to={`/view/${view.id}`}
      state={{ item: view }}
      key={view.id}
      style={{
        color: 'black',
        textDecoration: 'none',
        display: 'flex',
        justifyContent: index % 2 === 0 ? 'flex-start' : 'flex-end', // Alternates alignment of the entire block
      }}
    >
      <div style={{
        backgroundColor: 'black',
        padding: '20px',
        width: 'calc(50% - 20px)', // Adjusts width to half of the container minus gap
        textAlign: index % 2 === 0 ? 'left' : 'right', // Aligns text inside the card
      }}>
        <h1 style={{ color: 'white', fontSize: '2rem', fontWeight: 'bold' }}>{view.name}</h1>
        <h2 style={{ color: 'white', fontSize: '1rem', fontWeight: '100' }}>{view.whyImportant}</h2>
      </div>
      <hr style={{ padding: '0', margin: '20px 0', height: '2px', width: '100%' }} className="gradient" />
    </Link>
  ))}
</div>


      </section>
      {/* Floating Action Button for Adding a New Note */}
      <Link to="/create" style={{
        position: 'fixed',
        right: '20px',
        bottom: '20px',
        backgroundColor: 'black', // Example blue color
        color: 'white',
        padding: '15px',
        borderRadius: '50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        textDecoration: 'none',
        fontSize: '24px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
        zIndex: '1000', // Ensure it floats above other content
      }}>
        <span className="material-symbols-outlined">add</span>
      </Link>
    </div>
  );
};

export default Home;
